import React, { useState } from "react";
import DropdownOptions from "./DropdownOptions";
import ListTaskComponent from "./ListTaskComponent";
import ListUserComponent from "./ListUserComponent";



const AdminDashboard = () => {

  const [title,setTitle] = useState("Select user/task");
  const [tasks, setTasks] = useState([]);
  const [users, setUsers] = useState(users);
  const user = useUser();
  const options= [
    {value:"USERS",label:"Users"},
    {value:"TASKS",label:"Tasks"},
  ]

  useEffect(() => {
    TaskService.getTasksAdmin(user.jwt).then((tasks) =>{
      setTasks(tasks);
    });
    if (!user.jwt) {
      console.warn("No valid jwt found, redirecting to login page");
      navigate("/login");
    }
  }, []);

  useEffect(() => {
    UserService.getUsersAdmin(user.jwt).then((users) =>{
      setUsers(users);
    });
    if (!user.jwt) {
      console.warn("No valid jwt found, redirecting to login page");
      navigate("/login");
    }
  }, []);

  const handleEvent = (e) => {
    setTitle(e);
  }

  return (
    <div className="container">
      <DropdownOptions title={`${title}`} options={options} handleEvent={handleEvent}/>
      {
        (title === "USERS") ? <ListUserComponent users={users}/> : <ListTaskComponent tasks={tasks}/>
      }
    </div>
  );
};
export default AdminDashboard;
